('5df009c2cf69f', 'Andrewmindy', 'yourmail@gmail.com', 'Test, just a test', 'Hello. And Bye.', '2019-12-10', '04:10:26pm', 1),
('5df0aacad1ee8', 'Emmanuel Effiom Duke', 'starduke001@gmail.com', 'Feedback', 'Thank you E-Geniusquiz for your initiative. I was rewarded for partaking in the quiz.', '2019-12-11', '03:37:30am', 1),
('5e02dea704943', 'KOLAPO ELIJAH', 'Elijahkolapo@gmail.com', '', 'I don\'t what you are telling me o?', '2019-12-24', '10:59:35pm', 1),
